) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `great` */

/*Table structure for table `test` */

DROP TABLE IF EXISTS `test`;

CREATE TABLE `test` (
  `test` int(11) DEFAULT NULL
